// STYLES
import './component-styles/ProductOverview.css'

// HOOKS
import { useMediaSize } from '../hooks/useMediaSize';

const ProductOverview = ({ data, params }) => {
  const isolateProduct = (data) => {
    if(Array.isArray(data)) {
      const [ item ] = data.filter(item => {
        return item.slug === params.slug
      })
      return item
    } else {
      return data
    }    
  }
  const product = isolateProduct(data)
  const media = useMediaSize()
  const productImg = product.image[media]
  console.log(product.slug)
  console.log(media)


  return (
    <div className="product-overview">
      <div className="img-container">
        <img src={require(`../assets/product-${product.slug}/${media}/image-product.jpg`)} alt="product" />
      </div>
      <div className="info">
      {product.new && <span>NEW PRODUCT</span>}
      <h1 className="headline">{product.name.toUpperCase()}</h1>
      </div>
    </div>
  )
};

export default ProductOverview;
