import { useState, useEffect } from "react";

export const useMediaSize = () => {
  const [mediaSize, setMediaSize] = useState('')

  useEffect(() => {
    const queries = [
      '(max-width: 599px)', 
      '(max-width: 1199px)', 
      '(min-width: 1200px)'
    ]

    const currentMedia = queries.find(query => {
      return window.matchMedia(query).matches
    })

    console.log(currentMedia)

    switch (currentMedia) {
      case '(max-width: 599px)':
        return setMediaSize('mobile')
      case '(max-width: 1199px)':
        return setMediaSize('tablet')
      case '(min-width: 1200px)':
        return setMediaSize('desktop')
    }
  }, [mediaSize])

  return mediaSize
};
