// STYLES
import './component-styles/ProductDetails.css'

// HOOKS
import { useNavigate } from 'react-router-dom';
import { useFetch } from '../hooks/useFetch'

// COMPONENTS
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';
import Features from'./Features';
import InTheBox from './InTheBox';
import Gallery from './Gallery';
import SimilarProducts from './SimilarProducts';
import NavMenu from './NavMenu';
import About from './About';
import Footer from './Footer';
import ProductOverview from './ProductOverview';
import Shopping from './Shopping';

const ProductDetails = () => {
  const navigate = useNavigate()
  const params = useParams()
  const url = 'http://localhost:3001/products'
  const { data, isPending, error } = useFetch(url, params)

  const isolateProduct = (data) => {
    if(Array.isArray(data)) {
      const [ item ] = data.filter(item => {
        return item.slug === params.slug
      })
      return item
    } else {
      return data
    }    
  }
  const product = isolateProduct(data)

  return (
    <div className="product-details">
      <Navbar />
      <div className="content">
        <div className="navlink-container">
          <button onClick={() => navigate(-1)}>Go Back</button>
        </div>
        {error && <p className="error">{error}</p>}
        {isPending && <p className="loading"> Loading...</p>}
        {data && 
        <div className="async-container">
          <ProductOverview data={data} params={params} />
          <Features />
          <InTheBox />
          <Gallery />
          <SimilarProducts />
        </div>} 
        <NavMenu />
        <About />
        <Footer />
      </div>
    </div>
  )
};

export default ProductDetails;
