// STYLES
import "./component-styles/Checkout.css"

// HOOKS
import { useFetch } from "../hooks/useFetch"
const Checkout = () => {
  const { data, isPending, error } = useFetch()
  const cart = JSON.parse(localStorage.getItem("cart"))
  // for each item in cart, find item in data with same slug
  // if price is ===, return cart item
  const confirmCartItems = (cart, data)=> {
    const newCart = cart.map(item => {
      return data.find(element => {
        return element.slug === item.slug
      })
    })
    console.log(newCart)
  }

  return (
    <div className="checkout">
      {(cart && data) && confirmCartItems(cart, data)}
    </div>
  )
}

export default Checkout
