import { createContext, useReducer } from "react"


export const CartContext = createContext()

const CartReducer = (state, action) => {
  switch (action.type) {
    case 'SHOW_CART':
      return { ...state, showCart: action.payload }
    // case 'UPDATE_CART':
    //   return { ...state, showCart: action.payload }
    default: 
      return state
  }
}

export function CartProvider({ children }) {

  const initialState = {
    showCart: false,
    cartSize: 0
  }

  // const [state, dispatch] = useReducer(CartReducer, { showCart: false })

  const [state, dispatch] = useReducer(CartReducer, initalState)

  const setShowCart = (showCart) => {
    dispatch({ type: 'SHOW_CART', payload: showCart})
  }

  const setCartSize = (size) => {
    dispatch({ type: 'UPDATE_CART_SIZE', payload: cartSize})
  }

  return (
    <CartContext.Provider value={{...state, setShowCart}}>
      {children}
    </CartContext.Provider>
  )
}
