import { useState, useEffect } from "react"
import { useLocation, useParams } from "react-router-dom"

export const useFetch = () => {
  const [data, setData] = useState(null)
  const [isPending, setIsPending] = useState(false)
  const [error, setError] = useState(null)
  const [path, setPath] = useState(null)
  const params = useParams()
  // const { pathname } = useLocation()

  useEffect(() => {
    // abort controller for async data
    const controller = new AbortController()

    // create query string and add to url to fetch specific data
    const createQueryString = () => {
      let queryString = `?category=${params.category}`
      if(params.slug) {
        queryString += `&slug=${params.slug}`
      }
      return queryString
    }

    const queryString = createQueryString()
    const url = 'http://localhost:3001/products' + queryString

    setPath(url)

    const fetchData = async () => {
      setIsPending(true)

      try {
        const res = await fetch(path, { signal: controller.signal })
        if(!res.ok) {
          throw new Error(res.statusText)
        }
        const data = await res.json()

        setIsPending(false)
        setData(data)
        setError(null)
      } catch (err) {
        if (err.name === "AbortError") {
          console.log("the fetch was aborted")
        } else {
          setIsPending(false)
          setError('Could not fetch the data')
        }
      }
    }

    fetchData()

    return () => {
      controller.abort()
    }

  }, [path, params.category, params.slug])

  return { data, isPending, error }
}
