import { useState, useEffect } from "react";

export const useMediaSize = () => {
  const [mediaSize, setMediaSize] = useState('')

  useEffect(() => {
    const queries = [
      '(max-width: 599px)', 
      '(max-width: 1199px)', 
      '(min-width: 1200px)'
    ]

    const mqList = queries.find(query => {
      return window.matchMedia(query).matches
    })
    console.log(mqList)
    return mediaSize
  }, [])

  return mediaSize
};
