// STYLES
import { useEffect, useRef, useState } from "react"
import "./component-styles/Shopping.css"


const Shopping = ({ price }) => {
  const [quantity, setQuantity] = useState(null)
  const inputRef = useRef()

  // const increment = (e) => {
  //   e.preventDefault()
  //   if(inputRef.current.value >= 99) return false
  //   inputRef.current.value++
  // }

  return (
    <div className="shopping">
      <p className="price">$ {price.toLocaleString()}</p>
      <div className="add-container">
        <form className="shopping-form">
          <div className="quantity-btn">
            <button className="plus-minus" id="minus">-</button>
            <input 
              className="quantity-input" 
              id="amount" type="number" 
              // ref={inputRef}
              value="1"
              min="1" 
              max="100" 
              onChange={e => setQuantity(e.target.value)}
              required 
            />
            <button className="plus-minus" id="plus">+</button>
          </div>
          <button className="add-btn">ADD TO CART</button>
        </form>
      </div>    
    </div>
  )
}

export default Shopping
