// STYLES
import './component-styles/Includes.css'

const Includes = ({ includes }) => {
  const capitalize = (str) => {
    const arr = str.split(" ")
    // console.log(arr)
    return arr.map(str => {
      return str[0].toUpperCase() + str.substring(1)
    }).join(" ")
    // console.log(newArr.join(" "))
    // for (let i = 0; i < arr.length; i++) {
    //   console.log(arr[i][0].toUpperCase() + arr[i].substring(1))
    // }
  }

  capitalize(includes[3].item)

  return (
    <div className="includes">
      <h1 className="includes-headline">IN THE BOX</h1>
      {includes && includes.map(item => (
        <ul className="included-list" key={item.item}>
          <li className="included-list-item">
            <span className="included-amount">{item.quantity}x</span>
            <span className="included-item">{item.item.capitalize(item.item)}</span>
          </li>
        </ul>
      ))}
    </div>
  )
};

export default Includes;
