// Returns a string indicating the device type
// ie = 'mobile', 'tablet', or 'desktop'
// used to change images based on media by pulling from
// db object with device type as key and img path as value


import { useState, useLayoutEffect } from "react";

export const useMediaSize = (device) => {
  const [mediaSize, setMediaSize] = useState(device)

  useLayoutEffect(() => {
    const queries = [
      '(max-width: 599px)', 
      '(max-width: 1199px)', 
      '(min-width: 1200px)'
    ]

    // returns first string from queries array that matches media query
    const currentMedia = queries.find(query => {
      return window.matchMedia(query).matches
    })

    const currentDevice = (queryList) => {
      queryList.find(query => {
        return window.matchMedia(query).matches
      })
    }
    console.log(currentDevice(queries))

    const changeMedia = () => {
      switch (currentMedia) {
        case '(max-width: 599px)':
          return setMediaSize('mobile')
        case '(max-width: 1199px)':
          return setMediaSize('tablet')
        case '(min-width: 1200px)':
          return setMediaSize('desktop')
        default:
          return setMediaSize('mobile')
      }
    }

    // returns mediaQueryList object 
    const mql = matchMedia(currentMedia)
    // mql.onchange = changeMedia()
    console.log(mql)
    // mql.addEventListener('change', changeMedia(currentMedia), { once: true })

  }, [mediaSize])

  return mediaSize

};
