// STYLES
import { useRef } from "react"
import "./component-styles/Shopping.css"


const Shopping = ({ price, product }) => {
  const inputRef = useRef()

  const setValue = (e) => {
    inputRef.current.value = e.target.value
  }

  const increment = (e) => {
    e.preventDefault()
    if(inputRef.current.value >= 99) return false
    inputRef.current.value++
  }

  const decrement = (e) => {
    e.preventDefault()
    if(inputRef.current.value <= 1) return false
    inputRef.current.value--
  }

  const addItem = (cart, item) => {
    cart.push(item)
    localStorage.setItem("cart", JSON.stringify(cart))
  }

  const cartHasItem = (cart) => {
    return cart.some(item => {
      return item.name === product.name
    })
  }

  const updateItem = (cart) => {
    const [ cartItem ] = cart.filter(item => {
      return item.name === product.name
    })
    cartItem.quantity = parseInt(cartItem.quantity) + parseInt(inputRef.current.value)
    console.log(cart)
  }


  const handleSubmit = (e) => { 
    e.preventDefault()
    // if there is no cart, create cart and add item
    if(!localStorage.getItem("cart")) {
      const cart = []
      const item = product
      item.quantity = inputRef.current.value
      item.inCart = true
      addItem(cart, product)
    } else {
      const cart = JSON.parse(localStorage.getItem("cart"))
      if(cartHasItem(cart)) {
        updateItem(cart)
      } else {

      }
    }
  }

  // const thisItem = localStorage.getItem(product.name)
  // const itemAsObj = JSON.parse(thisItem)
  // console.log(itemAsObj)

  return (
    <div className="shopping">
      <p className="price">$ {price.toLocaleString()}</p>
      <div className="add-container">
        <form className="shopping-form" onSubmit={handleSubmit}>
          <div className="quantity-btn">
            <button className="plus-minus" id="minus"  onClick={decrement}>-</button>
            <input 
              className="quantity-input" 
              id="amount" 
              type="number" 
              ref={inputRef}
              defaultValue="1"
              min="1" 
              max="100" 
              onChange={e => setValue(e)}
              required 
            />
            <button className="plus-minus" id="plus"  onClick={increment}>+</button>
          </div>
          <button className="add-btn">ADD TO CART</button>
        </form>
      </div>    
    </div>
  )
}

export default Shopping
