// STYLES
import './component-styles/ProductOverview.css'

// HOOKS
import { useMediaSize } from '../hooks/useMediaSize';

const ProductOverview = ({ data, params }) => {
  // get current product object from database data
  const [ product ] = data.filter(product => {
    return product.slug === params.slug
  })

  // determine current device and pass to useMediaSize for responsiveness
  const getCurrentDevice = () => {
    const deviceWidth = window.innerWidth
    if(deviceWidth >= 1200) return 'desktop'
    if(deviceWidth >= 600) return 'tablet'
    return 'mobile'
  }
  const media = useMediaSize(getCurrentDevice())
  console.log(product.image.media)

  return (
    <div className="product-overview">
      <div className="img-container">
        <img src="" alt="" />
      </div>
    </div>
  )
};

export default ProductOverview;
