// STYLES
import "./component-styles/Checkout.css"

// HOOKS
import { useFetch } from "../hooks/useFetch"
import { useNavigate } from "react-router-dom"

// COMPONENTS
import Navbar from "./Navbar"
import Footer from "./Footer"
import { useRef, useState } from "react"


const Checkout = ({ cart }) => {
  const { data, isPending, error } = useFetch()
  // const cart = JSON.parse(localStorage.getItem("cart"))
  console.log(cart)

  // go back button logic
  const navigate = useNavigate()
  const handleClick = () => {
    navigate(-1)
  }

  // Radio button active state logic
  const [active, setActive] = useState(0)
  const radioRef = useRef()
  console.log(active)

  // compare cart to items in database and pull appropriate product
  // data from the database for security
  const confirmCartItems = (cart, data) => {
    // wait for the data
    if(!data) return false
    // check each item in cart
    const newCart = cart.map(item => {
      // return matching item from database
      const confirmedItem = data.find(element => {
        return element.slug === item.slug
      })
      // add quantity value to item
      confirmedItem.quantity = item.quantity
      return confirmedItem
    })
    return newCart
  }

  const confirmedItems = confirmCartItems(cart, data)
  // console.log(confirmedItems)

  return (
    <div className="checkout">
      <Navbar />
      <div className="content">
        <div className="navlink-container">
          <button onClick={handleClick}>Go Back</button>
        </div>
        <div className="checkout-container">
          <h1 className="title">CHECKOUT</h1>
          <form className="checkout-form">

            {/* BILLING DETAILS SECTION */}
            <h3 className="sup-head">BILLING DETAILS</h3>
            <label htmlFor="name">Name</label>
            <input type="text" placeholder="Ann Onymous"/>

            <label htmlFor="email">Email Address</label>
            <input type="email" placeholder="annonymous@email.com"/>

            <label htmlFor="phone">Phone Number</label>
            <input type="tel" placeholder="+1 202-555-0136"/>

            {/* SHIPPING INFO SECTION */}
            <h3 className="sup-head">SHIPPING INFO</h3>
            <label htmlFor="address">Your Address</label>
            <input type="text" placeholder="123 Imaginary Street"/>

            <label htmlFor="zip">ZIP Code</label>
            <input type="text" placeholder="10101"/>

            <label htmlFor="city">City</label>
            <input type="text" placeholder="Mordor"/>

            <label htmlFor="country">Country</label>
            <input type="text" placeholder="Middle-Earth"/>

            {/* PAYMENT DETAILS SECTION */}
            <h3 className="sup-head">PAYMENT DETAILS</h3>
            <label htmlFor="payment">Payment Method</label>

            <div className={`radio-container ${active ? "" : "active"}`}>
              <input type="radio" name="payment" checked={active==0} onChange={() => setActive(0)} />
              <label htmlFor="eMoney">e-Money</label>
            </div>

            <div className={`radio-container ${active ? "active" : ""}`}>
              <input type="radio" name="payment" checked={active==1} onChange={() => setActive(1)} />
              <label htmlFor="cod">Cash on Delivery</label>
            </div>

            <label htmlFor="eMoneyNumber">e-Money Number</label>
            <input type="text" placeholder="238521993"/>

            <label htmlFor="eMoneyPin">e-Money PIN</label>
            <input type="text" placeholder="1234"/>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  )
}

export default Checkout
