// STYLES
import './component-styles/CartMenu.css'

// HOOKS
import { useCart } from '../hooks/useCart'
import Quantity from './Quantity'

const CartMenu = () => {
  const cart = JSON.parse(localStorage.getItem("cart"))
  const { showCart, setShowCart } = useCart()
  console.log(cart)

  const handleClose = () => {
    showCart ? setShowCart(false) : setShowCart(true)
  }

  const checkTotals= () => {
    const total = cart.forEach(item => {
      console.log(item.price)
    })
  }

  checkTotals()

  const cartTotal = () => {
    const initialValue = 0;
    const total = cart.reduce((prevItem, currentItem) => {
      return prevItem + currentItem.price
    }, initialValue)
  }


  return (
    <div className="cart-menu-backdrop">
      <div className="cart-menu">
        <button className="close-btn" onClick={handleClose}>x</button>
        <div className="cart-header">
          <h3 className="cart-title">CART ({cart.length})</h3>
          <button className="remove-all">Remove all</button>
        </div>
        <div className="cart-contents">
          {cart && cart.map(item => (
            <div className="cart-item" key={item.slug}>
              <div className="img-container">
                <img src={require(`../assets/cart/image-${item.slug}.jpg`)} alt={`{product.slug} preview`}  />
              </div>
              <div className="item-info">
                <h5 className="item-name">{item.shortHand}</h5>
                <p className="item-price">${item.price}</p>
              </div>
              <Quantity />
            </div>
          ))}
        </div>
        <div className="total-display">
          <h4 className="total-title">TOTAL:</h4>
          <span className="total-amount">${() => cartTotal()}</span>
        </div>
        <button className="checkout-btn">CHECKOUT</button>
      </div>
    </div>
  )
}

export default CartMenu
