// STYLES
import "./component-styles/Shopping.css"


const Shopping = ({ price }) => {
  const form = document.querySelector('.shopping-form')
  const quantity = document.querySelector('.quantity-input')
  const minusBtn = document.querySelector('#minus')
  const plusBtn = document.querySelector('#plus')
  const addBtn = document.querySelector('.add-btn')

  const increment = (e) => {
    e.preventDefault()
    console.log(quantity)
  }


  return (
    <div className="shopping">
      <p className="price">$ {price.toLocaleString()}</p>
      <div className="add-container">
        <form className="shopping-form">
          <div className="quantity-btn">
            <button className="plus-minus" id="minus">-</button>
            <input className="quantity-input" type="number" value="1" min="1" max="100" required />
            <button className="plus-minus" id="plus" onClick={increment}>+</button>
          </div>
          <button className="add-btn">ADD TO CART</button>
        </form>
      </div>    
    </div>
  )
}

export default Shopping
