// HOOKS
import { useNavigate } from 'react-router-dom';

const SeeProductButton = ({ productRoute, product }) => {
  const navigate = useNavigate()
  const handleClick = () => {
    navigate(productRoute, { state: {product}})
  }

  return (
    <button 
      className="see-product" 
      onClick={handleClick}
    >SEE PRODUCT
    </button>
  )
};

export default SeeProductButton;
