import { useState, useEffect } from "react";

const queries = [
  "max-width: 599px",
  "max-width: 1199px",
  "min-width: 1200px"
]

export const useMediaSize = (query) => {
  const [mediaSize, setMediaSize] = useState(null)

  useEffect(() => {
    const [ media ] = queries.filter(query => {
      return window.matchMedia(query)
    })

    switch (media) {
      case "max-width: 599px":
        setMediaSize("mobile")
        break
      case "max-width: 1199px":
        setMediaSize("tablet")
        break
      case "min-width: 1200px":
        setMediaSize("desktop")
        break
    }

    // const listener =() => setMediaSize(media)

    // media.addEventListener('resize', listener)

    // return () => media.removeEventListener('resize', listener)
  }, [query, mediaSize])

  return mediaSize
};
